package de.emac.aoc;

import java.util.ArrayList;
import java.util.HashSet;

public class D06 {

    public static void main(String[] args) {
        Util.tStart(0);
        //String input = "zcfzfwzzqfrljwzlrfnpqdbhtmscgvjw";
        char[] input = "".toCharArray();
        check(4, input);
        check(14, input);
        Util.tEnd(0);
    }

    private static void check(int limit, char[] input) {
        ArrayList<Character> workList = new ArrayList<>(limit);
        for(int i=0; i < input.length; i++){
            if(workList.size() == limit){
                workList.remove(0);
            }
            workList.add(input[i]);
            if(new HashSet<>(workList).size() == limit){
                System.out.println("found "+limit+" @ pos: "+(i+1));
                break;
            };
        }
    }
}
